var _ = require('lodash');


var _new = function(nm, module) {
	var _name = null;
	var _services = [];
	var _dtos = [];

	if(nm === null || nm === undefined || nm.length < 1) {
		module.fatal('Controller Name Isn\'t Defined');
	} else {
		_name = nm;
	}

	var getName = function() {
		return _name;
	};

	var __addSrv = function(srv) {
		_services.push(srv);
	};

	var __addDTO = function(dto) {
		_dtos.push(dto);
	};

	var __getServices = function() {
		return _services;
	};

	var __getDTOs = function() {
		return _dtos;
	};

	return {
		'getName': getName,
		'__getServices': __getServices,
		'__getDTOs': __getDTOs,
		'__addSrv': __addSrv,
		'__addDTO': __addDTO
	}
}

module.exports = _new;